<?php
 namespace Doctrine\Tests\Common\Collections\Expr; use Doctrine\Common\Collections\Expr\ExpressionVisitor; use Doctrine\Common\Collections\Expr\Value; use PHPUnit_Framework_TestCase as TestCase; class ValueTest extends TestCase { public function testGetter() { $value = 'foo'; $valueExpression = new Value($value); $actualValue = $valueExpression->getValue(); $this->assertEquals($value, $actualValue); } public function testVisitor() { $visitor = $this->getMockForAbstractClass('Doctrine\Common\Collections\Expr\ExpressionVisitor'); $visitor ->expects($this->once()) ->method('walkValue'); $value = 'foo'; $valueExpression = new Value($value); $valueExpression->visit($visitor); } }