<?php
 namespace Doctrine\Common\Persistence\Mapping; class StaticReflectionService implements ReflectionService { public function getParentClasses($class) { return []; } public function getClassShortName($className) { if (strpos($className, '\\') !== false) { $className = substr($className, strrpos($className, "\\")+1); } return $className; } public function getClassNamespace($className) { $namespace = ''; if (strpos($className, '\\') !== false) { $namespace = strrev(substr( strrev($className), strpos(strrev($className), '\\')+1 )); } return $namespace; } public function getClass($class) { return null; } public function getAccessibleProperty($class, $property) { return null; } public function hasPublicMethod($class, $method) { return true; } } 