<?php
 namespace Doctrine\Common\Proxy\Exception; use UnexpectedValueException as BaseUnexpectedValueException; class UnexpectedValueException extends BaseUnexpectedValueException implements ProxyException { public static function proxyDirectoryNotWritable($proxyDirectory) { return new self(sprintf('Your proxy directory "%s" must be writable', $proxyDirectory)); } public static function invalidParameterTypeHint( $className, $methodName, $parameterName, \Exception $previous = null ) { return new self( sprintf( 'The type hint of parameter "%s" in method "%s" in class "%s" is invalid.', $parameterName, $methodName, $className ), 0, $previous ); } public static function invalidReturnTypeHint($className, $methodName, \Exception $previous = null) { return new self( sprintf( 'The return type of method "%s" in class "%s" is invalid.', $methodName, $className ), 0, $previous ); } } 