<?php
 namespace Doctrine\DBAL\Driver\DrizzlePDOMySql; use Doctrine\DBAL\Platforms\DrizzlePlatform; use Doctrine\DBAL\Schema\DrizzleSchemaManager; class Driver extends \Doctrine\DBAL\Driver\PDOMySql\Driver { public function connect(array $params, $username = null, $password = null, array $driverOptions = array()) { $conn = new Connection( $this->constructPdoDsn($params), $username, $password, $driverOptions ); return $conn; } public function createDatabasePlatformForVersion($version) { return $this->getDatabasePlatform(); } public function getDatabasePlatform() { return new DrizzlePlatform(); } public function getSchemaManager(\Doctrine\DBAL\Connection $conn) { return new DrizzleSchemaManager($conn); } public function getName() { return 'drizzle_pdo_mysql'; } } 