<?php
 namespace Doctrine\DBAL\Event; use Doctrine\DBAL\Platforms\AbstractPlatform; use Doctrine\DBAL\Schema\Column; use Doctrine\DBAL\Schema\TableDiff; class SchemaAlterTableRenameColumnEventArgs extends SchemaEventArgs { private $_oldColumnName; private $_column; private $_tableDiff; private $_platform; private $_sql = array(); public function __construct($oldColumnName, Column $column, TableDiff $tableDiff, AbstractPlatform $platform) { $this->_oldColumnName = $oldColumnName; $this->_column = $column; $this->_tableDiff = $tableDiff; $this->_platform = $platform; } public function getOldColumnName() { return $this->_oldColumnName; } public function getColumn() { return $this->_column; } public function getTableDiff() { return $this->_tableDiff; } public function getPlatform() { return $this->_platform; } public function addSql($sql) { if (is_array($sql)) { $this->_sql = array_merge($this->_sql, $sql); } else { $this->_sql[] = $sql; } return $this; } public function getSql() { return $this->_sql; } } 