<?php
 namespace Doctrine\DBAL\Event; use Doctrine\DBAL\Platforms\AbstractPlatform; use Doctrine\DBAL\Schema\Column; use Doctrine\DBAL\Schema\Table; class SchemaCreateTableColumnEventArgs extends SchemaEventArgs { private $_column; private $_table; private $_platform; private $_sql = array(); public function __construct(Column $column, Table $table, AbstractPlatform $platform) { $this->_column = $column; $this->_table = $table; $this->_platform = $platform; } public function getColumn() { return $this->_column; } public function getTable() { return $this->_table; } public function getPlatform() { return $this->_platform; } public function addSql($sql) { if (is_array($sql)) { $this->_sql = array_merge($this->_sql, $sql); } else { $this->_sql[] = $sql; } return $this; } public function getSql() { return $this->_sql; } } 