<?php
 namespace Doctrine\DBAL\Event; use Doctrine\DBAL\Platforms\AbstractPlatform; use Doctrine\DBAL\Schema\Table; class SchemaDropTableEventArgs extends SchemaEventArgs { private $_table; private $_platform; private $_sql = null; public function __construct($table, AbstractPlatform $platform) { if ( ! $table instanceof Table && !is_string($table)) { throw new \InvalidArgumentException('SchemaDropTableEventArgs expects $table parameter to be string or \Doctrine\DBAL\Schema\Table.'); } $this->_table = $table; $this->_platform = $platform; } public function getTable() { return $this->_table; } public function getPlatform() { return $this->_platform; } public function setSql($sql) { $this->_sql = $sql; return $this; } public function getSql() { return $this->_sql; } } 