<?php
 namespace Doctrine\DBAL\Event; use Doctrine\DBAL\Connection; use Doctrine\DBAL\Schema\Index; class SchemaIndexDefinitionEventArgs extends SchemaEventArgs { private $_index = null; private $_tableIndex; private $_table; private $_connection; public function __construct(array $tableIndex, $table, Connection $connection) { $this->_tableIndex = $tableIndex; $this->_table = $table; $this->_connection = $connection; } public function setIndex(Index $index = null) { $this->_index = $index; return $this; } public function getIndex() { return $this->_index; } public function getTableIndex() { return $this->_tableIndex; } public function getTable() { return $this->_table; } public function getConnection() { return $this->_connection; } public function getDatabasePlatform() { return $this->_connection->getDatabasePlatform(); } } 