<?php
 namespace Doctrine\DBAL\Exception; use Doctrine\DBAL\DBALException; class DriverException extends DBALException { private $driverException; public function __construct($message, \Doctrine\DBAL\Driver\DriverException $driverException) { $exception = null; if ($driverException instanceof \Exception) { $exception = $driverException; } parent::__construct($message, 0, $exception); $this->driverException = $driverException; } public function getErrorCode() { return $this->driverException->getErrorCode(); } public function getSQLState() { return $this->driverException->getSQLState(); } } 