<?php
 namespace Doctrine\DBAL\Platforms; class SQLServer2008Platform extends SQLServer2005Platform { public function getDateTimeTypeDeclarationSQL(array $fieldDeclaration) { return 'DATETIME2(6)'; } public function getDateTypeDeclarationSQL(array $fieldDeclaration) { return 'DATE'; } public function getTimeTypeDeclarationSQL(array $fieldDeclaration) { return 'TIME(0)'; } public function getDateTimeTzTypeDeclarationSQL(array $fieldDeclaration) { return 'DATETIMEOFFSET(6)'; } public function getDateTimeFormatString() { return 'Y-m-d H:i:s.u'; } public function getDateTimeTzFormatString() { return 'Y-m-d H:i:s.u P'; } public function getDateFormatString() { return 'Y-m-d'; } public function getTimeFormatString() { return 'H:i:s'; } protected function initializeDoctrineTypeMappings() { parent::initializeDoctrineTypeMappings(); $this->doctrineTypeMapping['datetime2'] = 'datetime'; $this->doctrineTypeMapping['date'] = 'date'; $this->doctrineTypeMapping['time'] = 'time'; $this->doctrineTypeMapping['datetimeoffset'] = 'datetimetz'; } protected function getReservedKeywordsClass() { return 'Doctrine\DBAL\Platforms\Keywords\SQLServer2008Keywords'; } } 