<?php
 namespace Doctrine\DBAL\Query; use Doctrine\DBAL\DBALException; class QueryException extends DBALException { static public function unknownAlias($alias, $registeredAliases) { return new self("The given alias '" . $alias . "' is not part of " . "any FROM or JOIN clause table. The currently registered " . "aliases are: " . implode(", ", $registeredAliases) . "."); } static public function nonUniqueAlias($alias, $registeredAliases) { return new self("The given alias '" . $alias . "' is not unique " . "in FROM and JOIN clause table. The currently registered " . "aliases are: " . implode(", ", $registeredAliases) . "."); } } 