<?php
 namespace Doctrine\DBAL\Sharding; use Doctrine\DBAL\DBALException; class ShardingException extends DBALException { static public function notImplemented() { return new self("This functionality is not implemented with this sharding provider.", 1331557937); } static public function missingDefaultFederationName() { return new self("SQLAzure requires a federation name to be set during sharding configuration.", 1332141280); } static public function missingDefaultDistributionKey() { return new self("SQLAzure requires a distribution key to be set during sharding configuration.", 1332141329); } static public function activeTransaction() { return new self("Cannot switch shard during an active transaction.", 1332141766); } static public function noShardDistributionValue() { return new self("You have to specify a string or integer as shard distribution value.", 1332142103); } static public function missingDistributionType() { return new self("You have to specify a sharding distribution type such as 'integer', 'string', 'guid'."); } } 