<?php
 namespace Doctrine\DBAL\Types; use Doctrine\DBAL\Platforms\AbstractPlatform; class BooleanType extends Type { public function getSQLDeclaration(array $fieldDeclaration, AbstractPlatform $platform) { return $platform->getBooleanTypeDeclarationSQL($fieldDeclaration); } public function convertToDatabaseValue($value, AbstractPlatform $platform) { return $platform->convertBooleansToDatabaseValue($value); } public function convertToPHPValue($value, AbstractPlatform $platform) { return $platform->convertFromBoolean($value); } public function getName() { return Type::BOOLEAN; } public function getBindingType() { return \PDO::PARAM_BOOL; } } 