<?php
 namespace Doctrine\Tests\DBAL; use Doctrine\DBAL\Configuration; use Doctrine\Tests\DbalTestCase; require_once __DIR__ . '/../TestInit.php'; class ConfigurationTest extends DbalTestCase { protected $config; public function setUp() { $this->config = new Configuration(); } public function testReturnsDefaultConnectionAutoCommitMode() { $this->assertTrue($this->config->getAutoCommit()); } public function testSetsDefaultConnectionAutoCommitMode() { $this->config->setAutoCommit(false); $this->assertFalse($this->config->getAutoCommit()); $this->config->setAutoCommit(0); $this->assertFalse($this->config->getAutoCommit()); } } 