<?php
 namespace GeckoPackages\Silex\Services\Config; use Pimple\Container; use Pimple\ServiceProviderInterface; class ConfigServiceProvider implements ServiceProviderInterface { private $name; public function __construct($name = 'config') { $this->name = $name; } public function register(Container $app) { $name = $this->name; $app[$name] = function ($app) use ($name) { return new ConfigLoader( $app, isset($app[$name.'.dir']) ? $app[$name.'.dir'] : null, isset($app[$name.'.format']) ? $app[$name.'.format'] : '%key%.json', isset($app[$name.'.cache']) ? $app[$name.'.cache'] : null, isset($app[$name.'.env']) ? $app[$name.'.env'] : null ); }; } } 