<?php
 namespace Silex\Application; use Symfony\Component\Form; use Symfony\Component\Form\Extension\Core\Type\FormType; use Symfony\Component\Form\FormBuilder; use Symfony\Component\OptionsResolver\OptionsResolver\FormTypeInterface; trait FormTrait { public function form($data = null, array $options = array(), $type = null) { return $this['form.factory']->createBuilder($type ?: FormType::class, $data, $options); } public function namedForm($name, $data = null, array $options = array(), $type = null) { return $this['form.factory']->createNamedBuilder($name, $type ?: FormType::class, $data, $options); } } 