<?php
 namespace Silex\Provider; use Pimple\Container; use Pimple\ServiceProviderInterface; use Silex\Api\EventListenerProviderInterface; use Silex\Provider\Locale\LocaleListener; use Symfony\Component\EventDispatcher\EventDispatcherInterface; class LocaleServiceProvider implements ServiceProviderInterface, EventListenerProviderInterface { public function register(Container $app) { $app['locale.listener'] = function ($app) { return new LocaleListener($app, $app['locale'], $app['request_stack'], isset($app['request_context']) ? $app['request_context'] : null); }; $app['locale'] = 'en'; } public function subscribe(Container $app, EventDispatcherInterface $dispatcher) { $dispatcher->addSubscriber($app['locale.listener']); } } 