<?php
 namespace Silex; use PHPUnit\Framework\TestCase; use Symfony\Component\HttpKernel\Client; use Symfony\Component\HttpKernel\HttpKernelInterface; abstract class WebTestCase extends TestCase { protected $app; protected function setUp() { $this->app = $this->createApplication(); } abstract public function createApplication(); public function createClient(array $server = array()) { if (!class_exists('Symfony\Component\BrowserKit\Client')) { throw new \LogicException('Component "symfony/browser-kit" is required by WebTestCase.'.PHP_EOL.'Run composer require symfony/browser-kit'); } return new Client($this->app, $server); } } 