<?php
 namespace Silex\Tests\Provider; use PHPUnit\Framework\TestCase; use Silex\Application; use Silex\Provider\SerializerServiceProvider; class SerializerServiceProviderTest extends TestCase { public function testRegister() { $app = new Application(); $app->register(new SerializerServiceProvider()); $this->assertInstanceOf("Symfony\Component\Serializer\Serializer", $app['serializer']); $this->assertTrue($app['serializer']->supportsEncoding('xml')); $this->assertTrue($app['serializer']->supportsEncoding('json')); $this->assertTrue($app['serializer']->supportsDecoding('xml')); $this->assertTrue($app['serializer']->supportsDecoding('json')); } } 