<?php
 namespace Symfony\Component\PropertyAccess; use Psr\Cache\CacheItemPoolInterface; class PropertyAccessorBuilder { private $magicCall = false; private $throwExceptionOnInvalidIndex = false; private $cacheItemPool; public function enableMagicCall() { $this->magicCall = true; return $this; } public function disableMagicCall() { $this->magicCall = false; return $this; } public function isMagicCallEnabled() { return $this->magicCall; } public function enableExceptionOnInvalidIndex() { $this->throwExceptionOnInvalidIndex = true; return $this; } public function disableExceptionOnInvalidIndex() { $this->throwExceptionOnInvalidIndex = false; return $this; } public function isExceptionOnInvalidIndexEnabled() { return $this->throwExceptionOnInvalidIndex; } public function setCacheItemPool(CacheItemPoolInterface $cacheItemPool = null) { $this->cacheItemPool = $cacheItemPool; return $this; } public function getCacheItemPool() { return $this->cacheItemPool; } public function getPropertyAccessor() { return new PropertyAccessor($this->magicCall, $this->throwExceptionOnInvalidIndex, $this->cacheItemPool); } } 