<?php
 namespace Symfony\Component\Security\Core\Exception; class AccessDeniedException extends \RuntimeException { private $attributes = array(); private $subject; public function __construct($message = 'Access Denied.', \Exception $previous = null) { parent::__construct($message, 403, $previous); } public function getAttributes() { return $this->attributes; } public function setAttributes($attributes) { $this->attributes = (array) $attributes; } public function getSubject() { return $this->subject; } public function setSubject($subject) { $this->subject = $subject; } } 