<?php
 namespace Symfony\Component\Security\Core\Tests\Resources; use PHPUnit\Framework\TestCase; class TranslationFilesTest extends TestCase { public function testTranslationFileIsValid($filePath) { if (class_exists('PHPUnit_Util_XML')) { \PHPUnit_Util_XML::loadfile($filePath, false, false, true); } else { \PHPUnit\Util\XML::loadfile($filePath, false, false, true); } $this->addToAssertionCount(1); } public function provideTranslationFiles() { return array_map( function ($filePath) { return (array) $filePath; }, glob(dirname(dirname(__DIR__)).'/Resources/translations/*.xlf') ); } } 