<?php
 namespace Symfony\Component\Security\Guard\Token; use Symfony\Component\Security\Core\Authentication\Token\AbstractToken; class PreAuthenticationGuardToken extends AbstractToken implements GuardTokenInterface { private $credentials; private $guardProviderKey; public function __construct($credentials, $guardProviderKey) { $this->credentials = $credentials; $this->guardProviderKey = $guardProviderKey; parent::__construct(array()); parent::setAuthenticated(false); } public function getGuardProviderKey() { return $this->guardProviderKey; } public function getCredentials() { return $this->credentials; } public function setAuthenticated($authenticated) { throw new \LogicException('The PreAuthenticationGuardToken is *never* authenticated.'); } } 