<?php
 namespace Symfony\Component\Security\Http\Authentication; use Symfony\Component\HttpFoundation\Request; use Symfony\Component\Security\Core\Exception\AuthenticationException; class CustomAuthenticationFailureHandler implements AuthenticationFailureHandlerInterface { private $handler; public function __construct(AuthenticationFailureHandlerInterface $handler, array $options) { $this->handler = $handler; if (method_exists($handler, 'setOptions')) { $this->handler->setOptions($options); } } public function onAuthenticationFailure(Request $request, AuthenticationException $exception) { return $this->handler->onAuthenticationFailure($request, $exception); } } 