<?php
 namespace Symfony\Component\Security\Http\Authentication; use Symfony\Component\Security\Core\Authentication\Token\TokenInterface; use Symfony\Component\HttpFoundation\Request; class CustomAuthenticationSuccessHandler implements AuthenticationSuccessHandlerInterface { private $handler; public function __construct(AuthenticationSuccessHandlerInterface $handler, array $options, $providerKey) { $this->handler = $handler; if (method_exists($handler, 'setOptions')) { $this->handler->setOptions($options); } if (method_exists($handler, 'setProviderKey')) { $this->handler->setProviderKey($providerKey); } } public function onAuthenticationSuccess(Request $request, TokenInterface $token) { return $this->handler->onAuthenticationSuccess($request, $token); } } 