<?php
 namespace Symfony\Component\Security\Http\Logout; use Symfony\Component\Security\Core\Authentication\Token\TokenInterface; use Symfony\Component\HttpFoundation\Response; use Symfony\Component\HttpFoundation\Request; class SessionLogoutHandler implements LogoutHandlerInterface { public function logout(Request $request, Response $response, TokenInterface $token) { $request->getSession()->invalidate(); } } 